// Dark mode toggle functionality
const themeToggle = document.getElementById('themeToggle');
const body = document.body;

// Check for saved theme or prefer-color-scheme
const prefersDarkScheme = window.matchMedia('(prefers-color-scheme: dark)');
const currentTheme = localStorage.getItem('theme');

// Set initial theme
if (currentTheme === 'dark' || (!currentTheme && prefersDarkScheme.matches)) {
    body.classList.add('dark-mode');
}

// Toggle theme
themeToggle.addEventListener('click', () => {
    body.classList.toggle('dark-mode');

    // Save theme preference
    if (body.classList.contains('dark-mode')) {
        localStorage.setItem('theme', 'dark');
    } else {
        localStorage.setItem('theme', 'light');
    }
});

// Update theme based on system preference changes
prefersDarkScheme.addEventListener('change', (e) => {
    if (!localStorage.getItem('theme')) {
        if (e.matches) {
            body.classList.add('dark-mode');
        } else {
            body.classList.remove('dark-mode');
        }
    }
});


// Mobile Menu Toggle
const mobileMenuBtn = document.querySelector('.mobile-menu-btn');
const navLinks = document.querySelector('.nav-links');

mobileMenuBtn.addEventListener('click', () => {
    navLinks.classList.toggle('active');
    mobileMenuBtn.innerHTML = navLinks.classList.contains('active')
        ? '<i class="fas fa-times"></i>'
        : '<i class="fas fa-bars"></i>';

    // Prevent body scrolling when menu is open
    if (navLinks.classList.contains('active')) {
        document.body.classList.add('menu-open');
    } else {
        document.body.classList.remove('menu-open');
    }
});


// Close mobile menu when clicking on a link
document.querySelectorAll('.nav-links a').forEach(link => {
    link.addEventListener('click', () => {
        navLinks.classList.remove('active');
        mobileMenuBtn.innerHTML = '<i class="fas fa-bars"></i>';
        document.body.classList.remove('menu-open');

        // Update active nav link
        document.querySelectorAll('.nav-links a').forEach(a => a.classList.remove('active'));
        link.classList.add('active');
    });
});

// Smooth scrolling for navigation links
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function (e) {
        e.preventDefault();

        const targetId = this.getAttribute('href');
        if (targetId === '#') return;

        const targetElement = document.querySelector(targetId);
        if (targetElement) {
            // Close mobile menu if open
            navLinks.classList.remove('active');
            mobileMenuBtn.innerHTML = '<i class="fas fa-bars"></i>';
            document.body.classList.remove('menu-open');

            window.scrollTo({
                top: targetElement.offsetTop - 100,
                behavior: 'smooth'
            });
        }
    });
});

// Active nav link on scroll
const sections = document.querySelectorAll('section');
const navItems = document.querySelectorAll('.nav-links a');

window.addEventListener('scroll', () => {
    let current = '';

    sections.forEach(section => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.clientHeight;

        if (pageYOffset >= sectionTop - 200) {
            current = section.getAttribute('id');
        }
    });

    navItems.forEach(item => {
        item.classList.remove('active');
        if (item.getAttribute('href').substring(1) === current) {
            item.classList.add('active');
        }
    });

    // Hide/show header on scroll
    const header = document.getElementById('header');
    if (window.scrollY > 100) {
        header.classList.remove('hidden');
    } else {
        header.classList.add('hidden');
    }
});

// Animated Counter
const counters = document.querySelectorAll('.stat-number');
const speed = 200;

const animateCounters = () => {
    counters.forEach(counter => {
        const target = +counter.getAttribute('data-count');
        const count = +counter.innerText;
        const increment = target / speed;

        if (count < target) {
            counter.innerText = Math.ceil(count + increment);
            setTimeout(animateCounters, 100);
        } else {
            counter.innerText = target;
        }
    });
};

// Create circular skills animation
function createSkillsOrbits() {
    const orbitsContainer = document.getElementById('skillsOrbits');

    // Clear any existing orbits (for re-initialization)
    orbitsContainer.innerHTML = '';

    // Skills data with icons and labels
    const skills = [
        { icon: 'fab fa-html5', name: 'HTML5' },
        { icon: 'fab fa-css3-alt', name: 'CSS3' },
        { icon: 'fab fa-js-square', name: 'JavaScript' },
        { icon: 'fab fa-php', name: 'PHP' },
        { icon: 'fas fa-database', name: 'MySQL' },
        { icon: 'fab fa-bootstrap', name: 'Bootstrap' },
        { icon: 'fab fa-linkedin', name: 'LinkedIn' },
        { icon: 'fab fa-figma', name: 'Figma' },
        { icon: 'fab fa-git-alt', name: 'Git' },
        { icon: 'fab fa-wordpress', name: 'WordPress' },
        { icon: 'fab fa-google', name: 'Google Ads' },
        { icon: 'fab fa-facebook', name: 'Meta Ads' },
        { icon: 'fas fa-search', name: 'SEO' },
        { icon: 'fas fa-chart-pie', name: 'Analytics' },
        { icon: 'fab fa-youtube', name: 'Youtube' },
        { icon: 'fas fa-paint-brush', name: 'Canva' },
        { icon: 'fas fa-code', name: 'VS Code' },
        { icon: 'fas fa-mobile-alt', name: 'Responsive' },
        { icon: 'fas fa-envelope-open-text', name: 'Email' },
        { icon: 'fas fa-hashtag', name: 'Social Media' }
    ];

    // Create orbits with fixed distances
    const orbits = [
        { className: 'skill-orbit skill-orbit-1', count: 6 },
        { className: 'skill-orbit skill-orbit-2', count: 7 },
        { className: 'skill-orbit skill-orbit-3', count: 4 },
        { className: 'skill-orbit skill-orbit-4', count: 3 }
    ];

    let skillIndex = 0;

    orbits.forEach((orbit, orbitIndex) => {
        const orbitEl = document.createElement('div');
        orbitEl.className = orbit.className;

        // Calculate positions for skills in this orbit
        for (let i = 0; i < orbit.count && skillIndex < skills.length; i++) {
            const skill = skills[skillIndex];

            // Use radians for angle calculation
            const angle = (i / orbit.count) * 2 * Math.PI;

            const badge = document.createElement('div');
            badge.className = 'skill-badge';
            badge.innerHTML = `<i class="${skill.icon}"></i>`;
            badge.setAttribute('data-name', skill.name);

            // Calculate position based on orbit index
            const baseRadius = 50; // Base radius percentage
            const radiusIncrement = 25; // Each orbit adds 25% radius
            const radius = baseRadius + (orbitIndex * radiusIncrement);

            // Convert polar coordinates to cartesian
            const x = 50 + radius * Math.cos(angle);
            const y = 50 + radius * Math.sin(angle);

            badge.style.left = `${x}%`;
            badge.style.top = `${y}%`;
            badge.style.transform = `translate(-50%, -50%)`;

            // Add hover effect with tooltip
            badge.addEventListener('mouseenter', function (e) {
                const tooltip = document.createElement('div');
                tooltip.className = 'skill-tooltip';
                tooltip.textContent = this.getAttribute('data-name');
                tooltip.style.position = 'absolute';
                tooltip.style.bottom = '120%';
                tooltip.style.left = '50%';
                tooltip.style.transform = 'translateX(-50%)';
                tooltip.style.background = body.classList.contains('dark-mode') ? 'var(--dark)' : 'var(--primary)';
                tooltip.style.color = 'white';
                tooltip.style.padding = '5px 10px';
                tooltip.style.borderRadius = '5px';
                tooltip.style.fontSize = '0.8rem';
                tooltip.style.whiteSpace = 'nowrap';
                tooltip.style.zIndex = '1000';
                tooltip.style.pointerEvents = 'none';

                this.appendChild(tooltip);
            });

            badge.addEventListener('mouseleave', function () {
                const tooltip = this.querySelector('.skill-tooltip');
                if (tooltip) tooltip.remove();
            });

            orbitEl.appendChild(badge);
            skillIndex++;
        }

        orbitsContainer.appendChild(orbitEl);
    });
}

// Intersection Observer for animations
const observerOptions = {
    threshold: 0.1,
    rootMargin: '0px 0px -100px 0px'
};

const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
        if (entry.isIntersecting) {
            // Add visible class for animation
            entry.target.classList.add('visible');

            // Start counters if it's stats section
            if (entry.target.id === 'stats') {
                animateCounters();
            }
        }
    });
}, observerOptions);

// Observe all animated elements
const animatedElements = document.querySelectorAll('.skill-card, .project-card, .journey-card, .stat-item, #stats');
animatedElements.forEach(el => observer.observe(el));

// Form submission
const contactForm = document.getElementById('contactForm');

if (contactForm) {
    contactForm.addEventListener('submit', function (e) {
        e.preventDefault();

        // Get form values
        const name = document.getElementById('name').value;
        const email = document.getElementById('email').value;
        const subject = document.getElementById('subject').value;
        const message = document.getElementById('message').value;

        // Animate form submission
        const submitBtn = contactForm.querySelector('.btn');
        const originalText = submitBtn.innerHTML;

        submitBtn.innerHTML = '<i class="fas fa-check"></i> Message Sent!';
        submitBtn.style.background = 'linear-gradient(135deg, var(--success), var(--accent))';

        // Create success animation
        createSuccessAnimation();

        // Reset form
        contactForm.reset();

        // Reset button after 3 seconds
        setTimeout(() => {
            submitBtn.innerHTML = originalText;
            submitBtn.style.background = '';
        }, 3000);
    });
}

// Success animation
function createSuccessAnimation() {
    const successIcon = document.createElement('div');
    successIcon.innerHTML = '<i class="fas fa-check-circle"></i>';
    successIcon.style.position = 'fixed';
    successIcon.style.top = '50%';
    successIcon.style.left = '50%';
    successIcon.style.transform = 'translate(-50%, -50%) scale(0)';
    successIcon.style.fontSize = '100px';
    successIcon.style.color = 'var(--success)';
    successIcon.style.zIndex = '9999';
    successIcon.style.pointerEvents = 'none';
    document.body.appendChild(successIcon);

    // Animate the icon
    const animation = successIcon.animate([
        { transform: 'translate(-50%, -50%) scale(0)', opacity: 0 },
        { transform: 'translate(-50%, -50%) scale(1.2)', opacity: 1 },
        { transform: 'translate(-50%, -50%) scale(1)', opacity: 1 },
        { transform: 'translate(-50%, -50%) scale(0)', opacity: 0 }
    ], {
        duration: 2000,
        easing: 'cubic-bezier(0.175, 0.885, 0.32, 1.275)'
    });

    // Remove icon after animation
    animation.onfinish = () => {
        successIcon.remove();
    };
}

// Scroll progress indicator
window.addEventListener('scroll', () => {
    const winScroll = document.body.scrollTop || document.documentElement.scrollTop;
    const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    const scrolled = (winScroll / height) * 100;

    // Update header background based on scroll
    const header = document.getElementById('header');
    if (header) {
        if (scrolled > 5) {
            header.style.background = body.classList.contains('dark-mode')
                ? 'rgba(15, 23, 42, 0.98)'
                : 'rgba(255, 255, 255, 0.98)';
            header.style.backdropFilter = 'blur(10px)';
        } else {
            header.style.background = body.classList.contains('dark-mode')
                ? 'rgba(15, 23, 42, 0.95)'
                : 'rgba(255, 255, 255, 0.95)';
            header.style.backdropFilter = 'blur(10px)';
        }
    }
});

// Initialize on page load
window.addEventListener('DOMContentLoaded', () => {
    // Create circular skills animation
    createSkillsOrbits();

    // Trigger initial animations for elements in viewport
    const checkVisibleElements = () => {
        document.querySelectorAll('.skill-card, .project-card, .journey-card, .stat-item').forEach(el => {
            const rect = el.getBoundingClientRect();
            const isVisible = (
                rect.top <= (window.innerHeight || document.documentElement.clientHeight) &&
                rect.bottom >= 0
            );

            if (isVisible) {
                el.classList.add('visible');
            }
        });
    };

    // Check on load
    checkVisibleElements();

    // Re-check on resize
    window.addEventListener('resize', checkVisibleElements);
});

// Handle window resize
window.addEventListener('resize', () => {
    // Recreate orbits on resize for better positioning
    setTimeout(createSkillsOrbits, 300);
});


// tabs add new projects
document.addEventListener('DOMContentLoaded', function () {
    const tabButtons = document.querySelectorAll('.tab-btn');
    const tabContents = document.querySelectorAll('.tab-content');

    // Initialize first tab as active
    activateTab('all');

    // Add click event to each tab button
    tabButtons.forEach(button => {
        button.addEventListener('click', () => {
            const tabId = button.getAttribute('data-tab');
            activateTab(tabId);
        });
    });

    function activateTab(tabId) {
        // Update active tab button
        tabButtons.forEach(button => {
            if (button.getAttribute('data-tab') === tabId) {
                button.classList.add('active');
            } else {
                button.classList.remove('active');
            }
        });

        // Show active tab content
        tabContents.forEach(content => {
            if (content.id === `${tabId}-projects` || (tabId === 'all' && content.id === 'all-projects')) {
                content.classList.add('active');
            } else {
                content.classList.remove('active');
            }
        });
    }
});



// Testimonials Slider
// Testimonials Slider - Simple Auto Rotation
document.addEventListener('DOMContentLoaded', function () {
    // 1. Testimonial Data - Fresher Experience with Indian Professionals
    const testimonials = [
        {
            text: "I was impressed with the dedication and willingness to learn. You quickly adapted to our workflow and delivered quality work within tight deadlines. Great potential!",
            name: "Sanjay Upadhya",
            role: "Senior Teacher, Tech Access",
            avatar: "https://cdn-icons-png.flaticon.com/512/18269/18269822.png"
        },
        {
            text: "You showed exceptional problem-solving skills. Your attention to detail in the frontend work was remarkable. Looking forward to seeing you grow in your career.",
            name: "Priya Sharma",
            role: "Project Manager, AssigNova",
            avatar: "https://cdn-icons-png.flaticon.com/512/417/417776.png"
        },
        {
            text: "Your understanding of modern web technologies as a fresher is impressive. You implemented complex features with minimal guidance. Keep up the good work!",
            name: "Mohd Aftab",
            role: "Social Media Manager, Digixfly",
            avatar: "https://cdn-icons-png.flaticon.com/512/18269/18269822.png"
        },
        {
            text: "Your design for our studio website was fresh and innovative. As a fresher, you showed great creativity and technical skill. The responsive design works perfectly across all devices.",
            name: "Arif Saifi",
            role: "Architect & Founder, Arc Square Design Studio",
            avatar: "https://cdn-icons-png.flaticon.com/512/18269/18269822.png"
        }
    ];

    // 2. Get HTML Elements
    const sliderTrack = document.getElementById('testimonialSlider');
    const timerIndicators = document.getElementById('timerIndicators');

    // Check if elements exist
    if (!sliderTrack || !timerIndicators) {
        console.log("Testimonials section not found");
        return;
    }

    // 3. Create Slides from Data
    function createSlides() {
        // Clear existing content
        sliderTrack.innerHTML = '';
        timerIndicators.innerHTML = '';

        // Create slides
        testimonials.forEach((testimonial, index) => {
            // Create slide HTML
            const slideHTML = `
                <div class="testimonial-slide">
                    <div class="quote-icon">
                        <i class="fas fa-quote-left"></i>
                    </div>
                    <p class="testimonial-text">${testimonial.text}</p>
                    <div class="client-info">
                        <div class="client-avatar">
                            <img src="${testimonial.avatar}" alt="${testimonial.name}">
                        </div>
                        <h3 class="client-name">${testimonial.name}</h3>
                        <p class="client-role">${testimonial.role}</p>
                    </div>
                </div>
            `;

            // Add slide to slider
            sliderTrack.innerHTML += slideHTML;

            // Create timer dot
            const dot = document.createElement('div');
            dot.className = 'timer-dot';
            if (index === 0) dot.classList.add('active');
            dot.dataset.slide = index;
            timerIndicators.appendChild(dot);
        });
    }

    // 4. Slider Variables
    let currentSlide = 0;
    const slideInterval = 5000;
    let slideTimer;

    // 5. Go to Specific Slide
    function goToSlide(slideNumber) {
        // Update current slide
        currentSlide = slideNumber;

        // Move slider
        sliderTrack.style.transform = `translateX(-${currentSlide * 100}%)`;

        // Update active dot
        const dots = document.querySelectorAll('.timer-dot');
        dots.forEach((dot, index) => {
            dot.classList.toggle('active', index === currentSlide);
        });
    }

    // 6. Next Slide Function
    function nextSlide() {
        // Calculate next slide (loop to first after last)
        const next = (currentSlide + 1) % testimonials.length;
        goToSlide(next);
    }

    // 7. Start Auto Rotation
    function startAutoSlide() {
        slideTimer = setInterval(nextSlide, slideInterval);
    }

    // 8. Stop Auto Rotation
    function stopAutoSlide() {
        clearInterval(slideTimer);
    }

    // 9. Initialize Slider
    function initSlider() {
        // Create slides and dots
        createSlides();

        // Start auto rotation
        startAutoSlide();

        // Add click events to dots
        const dots = document.querySelectorAll('.timer-dot');
        dots.forEach((dot, index) => {
            dot.addEventListener('click', function () {
                // Go to clicked slide
                goToSlide(index);

                // Reset auto slide timer
                stopAutoSlide();
                startAutoSlide();
            });
        });

        // Pause on hover
        sliderTrack.addEventListener('mouseenter', stopAutoSlide);
        sliderTrack.addEventListener('mouseleave', startAutoSlide);
    }

    // 10. Start Everything
    initSlider();

});

























// jquery
// Jquery
$(document).ready(function () {
    // 1. Smooth Scroll
    $('a[href^="#"]').click(function (e) {
        e.preventDefault();
        $('html,body').animate({
            scrollTop: $($(this).attr('href')).offset().top - 80
        }, 800);
    });

    // 2. Mobile Menu
    $('.mobile-menu-btn').click(function () {
        $('.nav-links').toggleClass('active');
        $(this).find('i').toggleClass('fa-bars fa-times');
    });

    // 3. Stats Counter
    $('.stat-number').each(function () {
        var t = $(this).data('count');
        $({ c: 0 }).animate({ c: t }, {
            duration: 2000,
            step: function () {
                $(this).text(Math.floor(this.c));
            }
        });
    });
});